/**
 * Custom Global CSS
 */ 
#IDX-main {width: 100%;}
.IDX-searchNavLink a{font-family: "Montserrat", Sans-serif !important;font-size: 11px !important;font-weight: 500 !important;letter-spacing: 2.6px !important;}
.IDX-searchNavItem IDX-nav-listingid {color:white !important;}
.IDX-searchNavItem .IDX-navLinkTitle{font-size: small !important;font-weight: 400 !important;}
.IDX-newSearch IDX-resultsAction IDX-btn IDX-btn--link IDX-btn--rounded{ border-radius: 3px !important;}
#idx-log-in{display:none !important;visibility:none !important;}
#idx-sign-up{display:none !important;visibility:none !important;}
:root{
--idx-color-green: #A7B8A9 !important;
--idx-theme-primary: var(--idx-color-green);
}
.IDX-wrapper-standard a{color:#000 !important}
.IDX-wrapper-standard .idx-page__details .idx-details__sidebar .idx-agent-info .IDX-btn:hover, .IDX-wrapper-standard .idx-page__details .idx-details__sidebar .idx-agent-info .IDX-btn:focus{background: var(--idx-color-green) !important}
.IDX-wrapper-standard .IDX-btn-primary{border:none !important}
.IDX-similar-listings--item-MLSLogo{display:none !imporatnt;visibility: hidden;}
.IDX-text.IDX-mlsSelectorRulesCourtesy,.idx-listing__courtesy,.idx-listings-total-results,.idx-max-listings-text,.IDX-results-title,.IDX-contentHeader p,.IDX-results-title--count,.IDX-MLSCourtesy,.IDX-MLSLogo,.idx-totalBaths,#s2id_IDX-bd,#IDX-propStatus-group,#IDX-tb-group,#s2id_IDX-add,#IDX-reduced-group,#s2id_IDX-searchRefinement,#IDX-propSubType-group,.IDX-advancedSearchColumn,#IDX-bd-group,#IDX-add-group,#IDX-searchRefinement-group,#IDX-tb-group,.contact__account-name{display:none !important;visibility: hidden;}
#IDX-contactAccountInfo::before,.idx-contact__listed-by-info::before{content: "Scout Real Estate Co.";font-size: 20px;margin: 0.5rem 0;order: 2;align-self: center;font-weight:bold;}
.IDX-contactAccountHeader{display:none !important;}
.IDX-resultsCellsContainer{padding-top:20px}
body .jupiterx-site{ font-family: "Outfit", Sans-serif;
    font-weight: 700;
}
.IDX-wrapper-standard{font-family: inherit;}
.IDX-wrapper-standard .IDX-results-refinement .IDX-refine-search--toggle:after{border-top: 5px solid #000;}
.idx-details-overview__address-full{line-height:1}
#idx-btn-primary{background-color:#000}
.IDX-wrapper-standard .IDX-topActions,#IDX-navbar {
    height: auto;
    line-height: 5em;
    font-size: larger;
    font-weight: 600;
}
.IDX-resultsContent{top: inherit !important;}

